import React, { useState } from 'react';
import './Profile.css';

export const Profile = () => {
  const [currentSlide, setCurrentSlide] = useState(1);

  const plusSlides = (index) => {
    setCurrentSlide((prevSlide) => prevSlide + index);
  };

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  return (
    <div className="slideshow-container">
      <div className={`mySlides fade ${currentSlide === 1 ? 'active' : ''}`}>
        <div className="numbertext">1 / 3</div>
        <img src="https://images.pexels.com/photos/8260442/pexels-photo-8260442.jpeg?auto=compress&cs=tinysrgb&w=600" 
        alt="Slide 1" style={{ width: '100%' }} />
        <div className="text">Caption Text 1</div>
      </div>

      <div className={`mySlides fade ${currentSlide === 2 ? 'active' : ''}`}>
        <div className="numbertext">2 / 3</div>
        <img src="https://images.pexels.com/photos/7579831/pexels-photo-7579831.jpeg?auto=compress&cs=tinysrgb&w=600"
         alt="Slide 2" style={{ width: '100%' }} />
        <div className="text">Caption Two</div>
      </div>

      <div className={`mySlides fade ${currentSlide === 3 ? 'active' : ''}`}>
        <div className="numbertext">3 / 3</div>
        <img src="https://images.pexels.com/photos/4031818/pexels-photo-4031818.jpeg?auto=compress&cs=tinysrgb&w=600"
         alt="Slide 3" style={{ width: '100%' }} />
        <div className="text">Caption Three</div>
      </div>

      <a className="prev" onClick={() => plusSlides(-1)}>&#10094;</a>
      <a className="next" onClick={() => plusSlides(1)}>&#10095;</a>
      <br />

      <div style={{ textAlign: 'center' }}>
        <span className="dot" onClick={() => goToSlide(1)}></span>
        <span className="dot" onClick={() => goToSlide(2)}></span>
        <span className="dot" onClick={() => goToSlide(3)}></span>
      </div>
    </div>
  );
};
